mapper.select:
select 
	no, id, name, rank, title, content, date, time, password
from board where no = #{no}

mapper.update:
update board
set name = #{name}, rank = #{rank},title = #{title},content = #{content}, id = #{id}, time = NOW(), date = NOW()
where no = #{no}

update board
set hit = hit+1
where no = #{no}

mapper.select:
select no,writer,date, content 
from comment 
where no= #{no}
